// Generuj różne wersje follow-up w zależności od numeru
const lead = $input.first().json;
const followupCount = lead.followup_count || 0;

let subject, body;

switch(followupCount) {
  case 0:
    // Pierwszy follow-up - przypomnienie
    subject = 'Re: Darmowa publikacja ofert pracy - przypomnienie';
    body = `
      <p>Dzień dobry,</p>
      <p>Piszę w nawiązaniu do mojego poprzedniego emaila dotyczącego <strong>bezpłatnej publikacji ofert pracy</strong> na platformie DlaMedica.pl.</p>
      <p>Rozumiem, że są Państwo zajęci, ale chciałbym się upewnić, że moja wiadomość dotarła.</p>
      <p>Krótkie przypomnienie korzyści:</p>
      <ul>
        <li>15 000+ aktywnych użytkowników - studentów i młodych lekarzy</li>
        <li>Darmowa publikacja podstawowych ogłoszeń</li>
        <li>Panel do zarządzania aplikacjami</li>
      </ul>
      <p>Czy mogę odpowiedzieć na jakiekolwiek pytania?</p>
    `;
    break;

  case 1:
    // Drugi follow-up - wartość dodana
    subject = 'Raport: Trendy rekrutacji w branży medycznej 2024';
    body = `
      <p>Dzień dobry,</p>
      <p>Przygotowaliśmy krótki raport na temat <strong>trendów rekrutacji w branży medycznej</strong>, który może być dla Państwa przydatny.</p>
      <p><strong>Kluczowe wnioski:</strong></p>
      <ul>
        <li>78% młodych lekarzy szuka pracy przez internet</li>
        <li>Ogłoszenia ze szczegółowym opisem warunków otrzymują 3x więcej aplikacji</li>
        <li>Najczęściej poszukiwane specjalizacje: interna, pediatria, POZ</li>
      </ul>
      <p>Chętnie podzielę się pełnym raportem - wystarczy odpowiedzieć na tego emaila.</p>
      <p>Przy okazji przypominam o możliwości <strong>darmowej publikacji ofert pracy</strong> na DlaMedica.pl.</p>
    `;
    break;

  case 2:
    // Trzeci follow-up - ostatnia szansa
    subject = 'Ostatnie przypomnienie - współpraca DlaMedica.pl';
    body = `
      <p>Dzień dobry,</p>
      <p>To moja ostatnia wiadomość w tej sprawie.</p>
      <p>Jeśli temat rekrutacji personelu medycznego nie jest dla Państwa aktualnie priorytetem, rozumiem to w pełni.</p>
      <p>Jeśli jednak kiedykolwiek będą Państwo potrzebować wsparcia w pozyskaniu:</p>
      <ul>
        <li>Lekarzy (wszystkie specjalizacje)</li>
        <li>Pielęgniarek i położnych</li>
        <li>Farmaceutów</li>
        <li>Fizjoterapeutów</li>
        <li>Techników medycznych</li>
      </ul>
      <p>Zapraszam do kontaktu. Nasze podstawowe usługi są <strong>całkowicie bezpłatne</strong>.</p>
      <p>Życzę powodzenia!</p>
    `;
    break;
}

return [{
  ...lead,
  followupSubject: subject,
  followupBody: body,
  nextFollowupCount: followupCount + 1
}];
